const url = $('Loop Over Items').first().json.navigationUrl

let profile_id = null;

if (url) {
  // Remove query params and trailing slash
  const cleanUrl = url.split('?')[0].replace(/\/$/, '');

  // Extract profile ID after /in/
  const match = cleanUrl.match(/linkedin\.com\/in\/([^/]+)/);

  if (match) {
    profile_id = match[1];
  }
}

return {
  profile_id,
};

// Get recipient name
const rawName = $('Loop Over Items').first().json.name || 'there';
const name = rawName.trim();

// Sender footer - CUSTOMIZE THIS
const footer = `\n\n‚Äî Your Name`;

// Message bodies for NON-connected users (connection requests)
// CUSTOMIZE THESE MESSAGES for your use case
const messages = [
  "Noticed you checked out my profile, so I thought I'd reach out and connect. I'm working on [YOUR PRODUCT] ‚Äî [BRIEF DESCRIPTION]. Happy to connect and share ideas.",

  "Saw that you viewed my profile ‚Äî always great to connect with like-minded professionals. I'm building [YOUR PRODUCT] to help with [VALUE PROP]. Would love to stay in touch.",

  "Thanks for stopping by my profile! I'm currently working on [YOUR PRODUCT], focused on [BENEFIT]. Let's connect and exchange insights.",

  "Noticed your visit on my profile and thought I'd say hello. I'm building [YOUR PRODUCT] to help [TARGET AUDIENCE]. Happy to connect!",

  "Hey there! I saw you viewed my profile and wanted to connect. I'm working on [YOUR PRODUCT] ‚Äî [BRIEF VALUE]. Looking forward to connecting."
];

// Pick random body
const body = messages[Math.floor(Math.random() * messages.length)];

// FINAL MESSAGE
const finalMessage = `Hey ${name},\n\n${body}${footer}`;

return {
  message: finalMessage,
};

