// --------------------------------
// 1. Transaction
// --------------------------------
const txn = $('Loop Over Items').first().json;

// --------------------------------
// 2. ItemRef
// --------------------------------
const itemRef = $('Collect All Item Mappings').first().json.itemRef;

// --------------------------------
// 3. Resolve CustomerRef DIRECTLY
// --------------------------------
let customer;

// Try existing customer first
try {
  customer = $('Get many customers').first().json;
} catch (_) {}

// If not found, use newly created customer
if (!customer?.Id) {
  customer = $('Create a Customer').first().json;
}

if (!customer?.Id) {
  throw new Error('Customer not resolved');
}

const customerRef = {
  value: customer.Id,
  name: customer.DisplayName
};

// --------------------------------
// 4. Validation
// --------------------------------
const amount = Number(txn.amount);

if (!amount || isNaN(amount)) {
  throw new Error('Invalid transaction amount');
}
if (!itemRef?.value) {
  throw new Error('ItemRef missing');
}

// --------------------------------
// 5. SalesReceipt Payload
// --------------------------------
const payload = {
  Line: [
    {
      LineNum: 1,
      Amount: amount,
      Description: txn.description || '',
      DetailType: 'SalesItemLineDetail',
      SalesItemLineDetail: {
        ItemRef: {
          value: itemRef.value,
          name: itemRef.name
        },
        Qty: 1,
        UnitPrice: amount,
        TaxCodeRef: { value: 'NON' }
      }
    }
  ],
  TxnDate: txn.date,
  PrivateNote: `${txn.description} - Ref: ${txn.referenceNumber || 'N/A'}`,
  CustomerRef: customerRef,
  DepositToAccountRef: { value: '35' },
  TotalAmt: amount
};

return { json: payload };

