  const created = $('Create Items').first().json;
  const item = created.Item || created;

  return [{
    json: {
      itemRef: {
        value: item.Id,
        name: item.Name
      }
    }
  }];
}

// --------------------------------
// 3. If existing item was found
// --------------------------------
const key = decision.desiredItemName;
const existing = decision.existingItemMapping[key];

// üîê SAFETY CHECK
