// --------------------------------
// 1. Inputs
// --------------------------------
const txn = $('Loop Over Items').first().json;
const qbItems = $input.all();

// --------------------------------
// 2. Utility
// --------------------------------
const normalize = s =>
  s.toLowerCase().replace(/[^a-z0-9]/g, ' ').trim();

const shortenName = (name) => {
  if (!name) return 'Service';
  return name
    .replace(/services?/gi, '')
    .replace(/limited|ltd|pvt|private/gi, '')
    .trim();
};

// --------------------------------
// 3. Desired item name
// --------------------------------
const desiredItemName = normalize(shortenName(txn.payee || txn.description));

// --------------------------------
// 4. Existing item map
// --------------------------------
const existingMap = {};

qbItems.forEach(n => {
  if (n.json?.Name && n.json?.Id) {
    existingMap[normalize(n.json.Name)] = {
      id: n.json.Id,
      name: n.json.Name
    };
  }
});

// --------------------------------
// 5. Decide create or reuse
// --------------------------------
const itemsToCreate = [];
const existingItemMapping = {};

if (existingMap[desiredItemName]) {
  existingItemMapping[desiredItemName] = existingMap[desiredItemName];
} else {
  itemsToCreate.push({
    name: shortenName(txn.payee),
    description: txn.description,
    type: 'Service',
    incomeAccountRef: '79',
    expenseAccountRef: '80'
  });
}

return [{
  json: {
    desiredItemName,
    itemsToCreate,
    existingItemMapping,
    needsCreation: itemsToCreate.length > 0
  }
}];

