// -----------------------------
// 1. Transaction
// -----------------------------
const txn = $('Loop Over Items').item.json;
const description = (txn.description || '').toLowerCase();

// -----------------------------
// 2. Vendor (from Merge logic)
// -----------------------------
const vendor = $input.first().json;

if (!vendor || !vendor.Id) {
  throw new Error(`Vendor not resolved for payee: ${txn.payee}`);
}

// -----------------------------
// 3. Load Categories (Chart of Accounts)
// -----------------------------
const categoryItems =
  $items('Search Categories')[0]?.json?.QueryResponse?.Account || [];

if (!categoryItems.length) {
  throw new Error('No categories found from QuickBooks');
}

// Build lookup map: name -> account
const categoryMap = {};
for (const acc of categoryItems) {
  if (acc.AccountType === 'Expense') {
    categoryMap[acc.Name.toLowerCase()] = acc;
  }
}

// -----------------------------
// 4. Auto Category Mapping Logic
// -----------------------------
let selectedCategory = null;

if (description.includes('upi') || description.includes('bank') || description.includes('charge')) {
  selectedCategory = categoryMap['bank charges'];
}
else if (description.includes('insurance')) {
  selectedCategory = categoryMap['insurance expense'];
}
else if (description.includes('ads') || description.includes('advertising')) {
  selectedCategory = categoryMap['advertising'];
}
else if (description.includes('vendor') || description.includes('service')) {
  selectedCategory = categoryMap['professional fees'];
}

// Default fallback
if (!selectedCategory) {
  selectedCategory = categoryMap['services'];
}

if (!selectedCategory) {
  throw new Error('No matching expense category found');
}

// -----------------------------
// 5. Build Expense Payload (CORRECT)
// -----------------------------
const expensePayload = {
  TxnDate: txn.date,
  PrivateNote: `${txn.description} - Ref: ${txn.referenceNumber || 'N/A'}`,
  EntityRef: {
    type: 'Vendor',
    value: vendor.Id,
    name: vendor.DisplayName || vendor.CompanyName
  },
  PaymentType: 'Cash',
  TotalAmt: txn.amount,
  Line: [
    {
      Amount: txn.amount,
      DetailType: 'AccountBasedExpenseLineDetail',
      AccountBasedExpenseLineDetail: {
        AccountRef: {
          value: selectedCategory.Id,
          name: selectedCategory.Name
        },
        BillableStatus: 'NotBillable',
        TaxCodeRef: {
          value: 'NON'
        }
      }
    }
  ]
};

return { json: expensePayload };
