const input = $json.body || {};
const errors = [];

/* ------------------ Helpers ------------------ */
const trim = (v) => (v == null ? '' : String(v).trim());

const capitalize = (v) =>
  trim(v)
    .toLowerCase()
    .replace(/\b\w/g, (c) => c.toUpperCase());

const isValidEmail = (email) =>
  /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email);

const normalizePhone = (phone) => {
  let v = trim(phone).replace(/[^0-9+]/g, '');

  // Default India handling
  if (v.length === 10) v = '+91' + v;

  if (!v.startsWith('+')) return '';
  if (v.length < 11 || v.length > 16) return '';

  return v;
};

const isValidName = (name) => /^[a-zA-Z\s'-]+$/.test(name);

/* ------------------ Cleaning ------------------ */
const firstName = capitalize(input.first_name);
const lastName = capitalize(input.last_name);
const email = trim(input.email).toLowerCase();
const phone = normalizePhone(input.phone);

/* ------------------ Validation ------------------ */

/* First Name */
if (!firstName) {
  errors.push({ field: 'first_name', message: 'First name is required' });
} else if (!isValidName(firstName)) {
  errors.push({ field: 'first_name', message: 'First name contains invalid characters' });
} else if (firstName.length < 2) {
  errors.push({ field: 'first_name', message: 'First name must be at least 2 characters long' });
}

/* Last Name */
if (!lastName) {
  errors.push({ field: 'last_name', message: 'Last name is required' });
} else if (!isValidName(lastName)) {
  errors.push({ field: 'last_name', message: 'Last name contains invalid characters' });
} else if (lastName.length < 2) {
  errors.push({ field: 'last_name', message: 'Last name must be at least 2 characters long' });
}

/* Email */
if (!email) {
  errors.push({ field: 'email', message: 'Email address is required' });
} else if (!isValidEmail(email)) {
  errors.push({ field: 'email', message: 'Email format is not valid' });
} else if (email.length > 254) {
  errors.push({ field: 'email', message: 'Email address is too long' });
}

/* Phone */
if (!phone) {
  errors.push({
    field: 'phone',
    message: 'Phone number is not valid or country code is missing'
  });
}

/* ------------------ Final Response ------------------ */
return [
  {
    json: {
      valid: errors.length === 0,
      errors,
      data: {
        first_name: firstName,
        last_name: lastName,
        email,
        phone
      }
    }
  }
];
