const unique = {};
const items = $input.all();

// Get domain from previous node
const domain = $('Set WooCommerce Domain').first().json.wc_domain;

for (const item of items) {
  const c = item.json.customer_id;

  if (!unique[c]) {
    unique[c] = {
      ...item.json,
      wc_domain: domain    // <-- add domain here
    };
  }
}

return Object.values(unique);

const orders = $input.all(); // all fetched orders

const customers = {};

for (const order of orders) {
  const orderData = order.json;

  // Ignore orders with total 0 or not completed
  if (parseFloat(orderData.total) <= 0 || orderData.status !== 'completed') continue;

  const custId = orderData.customer_id;

  if (!customers[custId]) {
    customers[custId] = {
      customer_id: custId,
      customer_name: `${orderData.billing.first_name} ${orderData.billing.last_name}`,
      lifetime_value: 0,
      total_orders: 0,
      order_ids: new Set() // track unique order ids
    };
  }

  // Count order only if it is unique
  if (!customers[custId].order_ids.has(orderData.id)) {
    customers[custId].order_ids.add(orderData.id);
    customers[custId].total_orders += 1;
    customers[custId].lifetime_value += parseFloat(orderData.total);
  }
}

// Assign tier and VIP reason
for (const custId in customers) {
  const cust = customers[custId];

  if (cust.lifetime_value >= 20000) {
    cust.tier = "Platinum";
    cust.vip_reason = "lifetime_value";
  } else if (cust.total_orders >= 5) {
    cust.tier = "Gold";
    cust.vip_reason = "total_orders";
  } else {
    cust.tier = "Silver";
    cust.vip_reason = null;
  }

  // Remove helper property
  delete cust.order_ids;
}

// Remove customers with invalid customer_id (guest checkouts)
const validCustomers = Object.values(customers).filter(c => c.customer_id && c.customer_id != 0);
return validCustomers;

