// Input
const text = $json?.text ?? $input.first().json?.text ?? "";
const dailyGoal = $('Config').item.json.DAILY_CALORIE_GOAL || 2000;

// Normalize text
const normalize = (s) =>
  String(s ?? "")
    .trim()
    .replace(/[Ôºö]/g, ":")
    .trim();

const toLines = (t) =>
  String(t)
    .replace(/\\n/g, "\n")
    .split(/\r?\n/)
    .map((l) => normalize(l))
    .filter(Boolean);

const lines = toLines(text);

// Key mapping
const KEY_MAP = {
  "food items": "foodItems",
  "meal type": "mealType",
  "estimated calories": "calories",
  "protein": "protein",
  "carbohydrates": "carbs",
  "fat": "fat",
  "fiber": "fiber",
  "health score": "healthScore",
  "positive points": "positivePoints",
  "improvement tips": "improvementTips",
  "advice": "advice"
};

const out = {
  date: $now.toFormat('yyyy-MM-dd'),
  time: $now.toFormat('HH:mm'),
  foodItems: "",
  mealType: "",
  calories: 0,
  protein: 0,
  carbs: 0,
  fat: 0,
  fiber: 0,
  healthScore: 0,
  positivePoints: "",
  improvementTips: "",
  advice: "",
  dailyGoal: dailyGoal,
  remainingCalories: dailyGoal
};

for (const line of lines) {
  const m = line.match(/^(.+?):\s*(.+)$/);
  if (!m) continue;

  const rawKey = m[1].toLowerCase().trim();
  const value = m[2].trim();

  const mappedKey = KEY_MAP[rawKey];
  if (!mappedKey) continue;

  // Parse numeric values
  if (["calories", "protein", "carbs", "fat", "fiber", "healthScore"].includes(mappedKey)) {
    const numMatch = value.match(/([\d.]+)/);
    out[mappedKey] = numMatch ? parseFloat(numMatch[1]) : 0;
  } else {
    out[mappedKey] = value;
  }
}

// Calculate remaining calories
out.remainingCalories = out.dailyGoal - out.calories;

// Generate health emoji based on score
const score = out.healthScore;
if (score >= 8) {
  out.healthEmoji = "üåü";
  out.healthLevel = "Excellent";
} else if (score >= 6) {
  out.healthEmoji = "‚úÖ";
  out.healthLevel = "Good";
} else if (score >= 4) {
  out.healthEmoji = "‚ö†Ô∏è";
  out.healthLevel = "Fair";
} else {
  out.healthEmoji = "üî¥";
  out.healthLevel = "Needs Improvement";
}

return [{ 
  json: out,
  binary: $input.first().binary
}];

