const inputText = $input.first().json.body?.text || $input.first().json.text || '';

// Check if text is provided
if (!inputText || inputText.trim() === '') {
  return [{
    json: {
      isValid: false,
      error: 'No text provided',
      text: inputText
    }
  }];
}

// Check for Chinese characters (Unicode ranges for Chinese)
const chineseRegex = /[\u4e00-\u9fff\u3400-\u4dbf\u{20000}-\u{2a6df}\u{2a700}-\u{2b73f}\u{2b740}-\u{2b81f}\u{2b820}-\u{2ceaf}\uf900-\ufaff\u3300-\u33ff\ufe30-\ufe4f\uf900-\ufaff\u{2f800}-\u{2fa1f}]/u;

const hasChinese = chineseRegex.test(inputText);

if (!hasChinese) {
  return [{
    json: {
      isValid: false,
      error: 'No Chinese characters detected in the input text',
      text: inputText
    }
  }];
}

// Validation passed
return [{
  json: {
    isValid: true,
    text: inputText,
    characterCount: inputText.length
  }
}];

const translations = $input.first().json.output?.translations || [];

if (!translations || translations.length === 0) {
  throw new Error('No translations found in the AI output');
}

// Create separate items for each translation
const items = translations.map(translation => ({
  json: {
    language: translation.language,
    text: translation.text,
    originalText: $input.first().json.text
  }
}));

return items;

