function parseJSONString(jsonString) {
  try {
    let cleanString = jsonString.trim()
      .replace(/^```json\n?/g, '')
      .replace(/\n?```$/g, '')
      .trim();


    const parsedData = JSON.parse(cleanString);

    if (typeof parsedData !== 'object' || parsedData === null) {
      return {
        success: false,
        error: "Parsed data is not a valid object",
        data: null
      };
    }

    return {
      success: true,
      error: null,
      data: parsedData
    };
  } catch (error) {
    return {
      success: false,
      error: error.message,
      data: null
    };
  }
}

