// Extract video URLs from Pixabay response
const videos = $input.item.json.hits;
const clips = [];

if (!videos || videos.length === 0) {
  throw new Error('No videos found for this search term');
}

for (let i = 0; i < videos.length; i++) {
  const video = videos[i];

  // Get the largest available video (usually 'large' or 'medium')
  const videoUrl = video.videos?.large?.url || video.videos?.medium?.url || video.videos?.small?.url;

  if (videoUrl) {
    clips.push({
      url: videoUrl,
      duration: video.duration || 15, // Default to 15 seconds if not provided
      id: video.id,
      width: video.videos?.large?.width || 1920,
      height: video.videos?.large?.height || 1080
    });
  }
}

if (clips.length === 0) {
  throw new Error('No valid video URLs found');
}

return clips.map(clip => ({ json: clip }));

// Get video clips from input
const clips = $input.all().map(item => item.json);

// Get the voiceover file ID from Google Drive (Download file node)
const driveFileId = $('Upload Voiceover to Drive').first().json.id;

// Build the direct download URL for the voiceover
const voiceoverUrl = `https://drive.google.com/uc?export=download&id=${driveFileId}`;

// Define different transitions for variety
const transitions = [
  "fade",
  "wipeRight",
  "slideLeft",
  "slideUp",
  "carouselLeft",
  "zoom",
  "shuffleTopRight",
  "wipeLeft",
  "slideDown",
  "carouselRight"
];

// Calculate clip durations with overlap for smooth transitions
const totalDuration = 300; // 5 minutes
const transitionDuration = 0.8; // 0.8 second overlap for smooth effect
const clipDuration = (totalDuration + (transitionDuration * (clips.length - 1))) / clips.length;

// Build Shotstack timeline
const timeline = {
  soundtrack: {
    src: voiceoverUrl,
    effect: "fadeInFadeOut",
    volume: 1.0
  },
  tracks: [
    {
      clips: clips.map((clip, index) => ({
        asset: {
          type: "video",
          src: clip.url
        },
        start: (index * clipDuration) - (index * transitionDuration),
        length: clipDuration,
        fit: "crop",
        scale: 1,
        transition: {
          in: transitions[index % transitions.length], // Different transition for each clip
          out: transitions[index % transitions.length]
        }
      }))
    }
  ]
};

const render = {
  timeline: timeline,
  output: {
    format: "mp4",
    resolution: "hd",
    fps: 25,
    quality: "high"
  }
};

return [{ json: render }];
