parameters.text:
=Package/Visitor Details:
- Type: {{ $json.packageType }}
- Description: {{ $json.description }}
- Is Visitor: {{ $json.isVisitor }}
- Arrival Time: {{ $json.arrivalTime }}

=:package: *{{ $json.isVisitor ? "Visitor" : "Package" }} Arrival - {{ $json.urgency === "high" ? "HIGH URGENCY" : "Regular" }}*

*Tenant:* {{ $json.tenantName }}
*Building:* {{ $json.building }}
*Type:* {{ $json.packageType }}
*Arrival:* {{ $json.arrivalTime }}
*Description:* {{ $json.description }}
*Urgency:* {{ $json.reason }}

Notification sent to tenant via {{ $json.urgency === "high" ? "email and SMS" : "email" }}.

parameters.inputSchema:
{
	"type": "object",
	"properties": {
		"urgency": {
			"type": "string",
			"enum": ["high", "regular"],
			"description": "The urgency level of the notification"
		},
		"reason": {
			"type": "string",
			"description": "Explanation for why this urgency level was assigned"
		}
	},
	"required": ["urgency", "reason"]
}
