const TENANT_DOMAINS = $('Set Variables').first().json.tenantDomains

function normStr(x) {
  return (x ?? "").toString().trim();
}

function isExternalLoginName(loginName) {
  const ln = normStr(loginName).toLowerCase();
  // Strong SPO guest signals
  return ln.includes("#ext#") || ln.includes("urn:spo:guest");
}

function domainFromEmail(email) {
  const e = normStr(email).toLowerCase();
  const at = e.lastIndexOf("@");
  return at >= 0 ? e.slice(at + 1) : "";
}

function isInternalDomain(domain) {
  const d = normStr(domain).toLowerCase();
  if (!d) return false;
  return TENANT_DOMAINS.some(td => td.toLowerCase() === d);
}

function isExternalEmail(email) {
  if (!TENANT_DOMAINS.length) return false; // domains disabled
  const d = domainFromEmail(email);
  if (!d) return false;
  return !isInternalDomain(d);
}

function itemType(item) {
  if (item?.folder) return "folder";
  if (item?.file) return "file";
  return "unknown";
}

const results = [];

for (const inItem of $input.all()) {
  const json = inItem.json;

  const spItem = json.item || {};
  const perms = json.permissions?.value || [];

  let hasAnonymousLink = false;
  let hasExternalPrincipal = false;

  const anonymousLinks = [];     // link permissions
  const externalPrincipals = []; // external users detected

  for (const p of perms) {
    // ---- 1) Anonymous links ----
    const link = p?.link;
    if (link && normStr(link.scope).toLowerCase() === "anonymous") {
      hasAnonymousLink = true;
      anonymousLinks.push({
        permissionId: p.id ?? null,
        roles: p.roles ?? [],
        linkType: link.type ?? null,        // view/edit
        webUrl: link.webUrl ?? null,
        hasPassword: p.hasPassword ?? null,
        preventsDownload: link.preventsDownload ?? null,
      });
    }

    // ---- 2) External/guest principals ----
    // Graph commonly includes external users under grantedToIdentitiesV2[].siteUser.loginName with #ext#
    const granted = Array.isArray(p?.grantedToIdentitiesV2) ? p.grantedToIdentitiesV2 : [];

    for (const ident of granted) {
      const loginName = ident?.siteUser?.loginName;
      const email = ident?.user?.email || ident?.siteUser?.email;

      const externalByLogin = loginName ? isExternalLoginName(loginName) : false;
      const externalByEmail = email ? isExternalEmail(email) : false;

      if (externalByLogin || externalByEmail) {
        hasExternalPrincipal = true;
        externalPrincipals.push({
          permissionId: p.id ?? null,
          roles: p.roles ?? [],
          displayName: ident?.user?.displayName || ident?.siteUser?.displayName || null,
          email: email ?? null,
          loginName: loginName ?? null,
          linkScope: link?.scope ?? null,
          linkType: link?.type ?? null,
          linkWebUrl: link?.webUrl ?? null,
        });
      }
    }
  }

  // Drop items that have neither anonymous nor external principals
  if (!hasAnonymousLink && !hasExternalPrincipal) continue;

  const out = {
    // Item identity / navigation
    name: spItem.name ?? null,
    type: itemType(spItem),
    webUrl: spItem.webUrl ?? null,
    lastModifiedDateTime: spItem.lastModifiedDateTime ?? null,

    // IDs that help you trace back
    itemId: spItem.id ?? null,
    driveId: spItem.parentReference?.driveId ?? null,
    siteId: spItem.parentReference?.siteId ?? null,
    parentId: spItem.parentReference?.id ?? null,
    libraryName: spItem.parentReference?.name ?? null,

    // Flags + details
    externalSharing: {
      hasAnonymousLink,
      hasExternalPrincipal,
    },
    details: {
      anonymousLinks,
      anonymousLinkCount: anonymousLinks.length,
      externalPrincipals,
      externalPrincipalCount: externalPrincipals.length,
    },
  };

  results.push({ json: out });
}

return results;
