parameters.jsCode:
// data from webhook
const data = $node["Receive Document Request (Webhook)"].json;

// html template from Postgres
const html = $node["Load HTML Template"].json.html_template;

function fillTemplate(template, data) {
  return template.replace(/{{\s*([\w.\[\]0-9]+)\s*}}/g, (match, path) => {
    try {
      return path
        .replace(/\[(\d+)\]/g, '.$1') // support items[0].field
        .split('.')
        .reduce((obj, key) => obj?.[key], data) ?? match;
    } catch {
      return match;
    }
  });
}

const filledHtml = fillTemplate(html, data);

return [
  {
    json: {
      html: filledHtml,
      filename: `${data.documentType}_${data.request.id}`
    }
  }
];

