const memoryData = $input.first().json;
const content = memoryData.content || memoryData;
const episodicMemory = content.episodic_memory || {};
const longTermEpisodes = episodicMemory.long_term_memory?.episodes || [];
const shortTermEpisodes = episodicMemory.short_term_memory?.episodes || [];
const allMemories = [...longTermEpisodes, ...shortTermEpisodes];

const sortedMemories = allMemories.sort((a, b) => {
  const timeA = a.timestamp || a.created_at || a.metadata?.timestamp || '';
  const timeB = b.timestamp || b.created_at || b.metadata?.timestamp || '';
  return new Date(timeA).getTime() - new Date(timeB).getTime();
});

const conversationHistory = sortedMemories
  .filter(m => m.content && m.content.trim() !== '')
  .slice(-20)
  .map(memory => {
    const role = memory.producer_role || memory.role || 'unknown';
    return `[${role}]: ${memory.content}`;
  }).join('\n');

const inputData = $('4. Store User Query').first()?.json || {};

return [{
  json: {
    text: inputData.text,
    chat_id: inputData.chat_id,
    customer_email: inputData.customer_email,
    username: inputData.username,
    conversation_history: conversationHistory || 'No previous history.',
    total_memories: allMemories.length
  }
}];
