// Handle both single-item and bulk webhook payloads
const d = Array.isArray($json.body)
  ? $json.body[$itemIndex]
  : $json.body ?? $json;

// Hard validation
if (!d || !d.order_id) {
  throw new Error('Invalid input: order data missing');
}

return {
  order_id: d.order_id,
  customer_email: d.customer_email,
  order_value: Number(d.order_value ?? 0),
  currency: d.currency,
  refund_count_30d: Number(d.refund_count_30d ?? 0),
  refund_count_lifetime: Number(d.refund_count_lifetime ?? 0),
  account_age_days: Number(d.account_age_days ?? 0),
  billing_country: d.billing_country,
  shipping_country: d.shipping_country,
  country_mismatch: d.billing_country !== d.shipping_country,
  risk_processed: d.risk_processed,
  rowNumber: Number(d.rowNumber),
  source: d.source,
  triggeredAt: d.triggeredAt
};

let aiRaw;
let ai;

// 1Ô∏è‚É£ Extract AI response
const aiText = $json.output?.[0]?.content?.[0]?.text;
if (!aiText) {
  throw new Error('AI response missing');
}

aiRaw = aiText
  .replace(/```json/gi, '')
  .replace(/```/g, '')
  .trim();

ai = JSON.parse(aiRaw);

// 2Ô∏è‚É£ Normalize AI fields
const riskLevel = (
  ai.risk_level ||
  ai.risk_class ||
  ai.risk_classification ||
  ''
).toUpperCase();

const drivers =
  ai.key_risk_drivers ||
  ai.drivers ||
  [];

// 3Ô∏è‚É£ Get ORIGINAL item using paired index
const original = $items("Normalize Data")[0].json;

// 4Ô∏è‚É£ Return merged object
return {
  // üîπ ORIGINAL DATA
  order_id: original.order_id,
  customer_email: original.customer_email,
  rowNumber: original.rowNumber,
  order_value: `${original.order_value} ${original.currency}`,

  // üîπ AI OUTPUT
  risk_score: Number(ai.risk_score ?? 0),
  risk_level: riskLevel,
  risk_drivers: Array.isArray(drivers) ? drivers.join(', ') : '',
  recommendation_action:
    ai.recommended_action ||
    ai.recommended_preventive_action ||
    '',

  // üîπ META
  ai_model_used: 'openai'
};

