// Split HR API array into one item per employee
const employees = items[0].json.body?.employees || [];
return employees.map(e => ({ json: e }));

// Map fields and add helper flags
const emp = items[0].json;
return [
  {
    json: {
      employeeId: emp.id || emp.employeeId,
      name: `${emp.firstName} ${emp.lastName}`.trim(),
      email: emp.email,
      department: emp.department || 'Unknown',
      status: emp.status || 'inactive',
      active: (emp.status || '').toLowerCase() === 'active',
      slackHandle: emp.slackHandle || '',
      teamsMessage: '',
      slackMessage: ''
    }
  }
];
