const labels = [];
const data = [];
const colors = [];

items.forEach((item, index) => {
  labels.push(
    `${item.json.sentiment} ${index + 1}`
  );

  data.push(1);

  if (item.json.sentiment === 'Positive') {
    colors.push('#4CAF50'); 
  } else if (item.json.sentiment === 'Negative') {
    colors.push('#F44336'); 
  } else {
    colors.push('#FFC107'); 
  }
});

return [{
  json: {
    labels,
    data,
    colors
  }
}];
