// Get input string from previous node
const input = $('Merge').first().json.topic;
const classroomName = typeof input === 'string' ? input.trim() : '';

// Get courses
const payload = $('Get All Classrooms').first().json;
const courses = payload.courses || [];

// Filter ACTIVE courses only
const activeCourses = courses.filter(course =>
  course.courseState === 'ACTIVE'
);

// Match (case-insensitive, exact)
const target = classroomName.toLowerCase();
const matchedCourse = activeCourses.find(course =>
  course.name.trim().toLowerCase() === target
);

if (matchedCourse) {
  return {
    json: {
      status: "Found",
      courseId: matchedCourse.id,
      courseName: matchedCourse.name
    }
  };
}

return {
  json: {
    status: "Not Found",
    searchedName: classroomName,
    activeCourseCount: activeCourses.length
  }
};

const topics = $input.item.json.topic || [];
const topicName = $('Set Parameters').first().json.desiredTopic;
const targetTopic = topics.find(t => t.name === topicName);

if (targetTopic) {
  return {
    status: "found",
    topicId: targetTopic.topicId
  };
} else {
  return {
    status: "not found"
  };
}

const zoomPayload = $('Zoom Webhook').item.json.body.payload.object;
const desiredThreshold = $input.first().json.durationThreshold;

if (!zoomPayload || !zoomPayload.recording_files || zoomPayload.recording_files.length === 0) {
  return [{ json: { status: false, reason: "no_recording_data" } }];
}

// Get any recording file with start/end times
const recordingFile = zoomPayload.recording_files[0];

if (!recordingFile.recording_start || !recordingFile.recording_end) {
  return [{ json: { status: false, reason: "missing_timestamps" } }];
}

// Calculate duration in minutes
const start = new Date(recordingFile.recording_start);
const end = new Date(recordingFile.recording_end);
const durationMinutes = (end - start) / 1000 / 60;

// Filter out recordings under 30 minutes
if (durationMinutes < desiredThreshold) {
  return [{ json: { status: false, reason: "too_short", durationMinutes } }];
}

return [{ json: { status: true, durationMinutes } }];

const payload = $input.first().json;

const activeCourseNames = payload.courses
  .filter(course => course.courseState === "ACTIVE")
  .map(course => course.name);

return [{ json: { courses: activeCourseNames } }];

const startTime = $('Zoom Webhook').item.json.body.payload.object.start_time;
const date = new Date(startTime);

// Get date parts without leading zeros
const year = date.getFullYear();
const month = date.getMonth() + 1;  // getMonth() returns 0-11
const day = date.getDate();

const title = `${day}.${month}.${year}`;

return [{ json: { title } }];
