const items = $input.all();
const results = [];

for (const item of items) {
  const current = item.json;

  const temp = current.main?.temp || 0;
  const humidity = current.main?.humidity || 0;
  const windSpeed = current.wind?.speed || 0;
  const weatherDesc = current.weather?.[0]?.description || 'unknown';
  const weatherMain = current.weather?.[0]?.main || 'unknown';

  const aqi = current.list?.[0]?.main?.aqi || 1;
  const aqiLabels = ['Good', 'Fair', 'Moderate', 'Poor', 'Very Poor'];
  const aqiLabel = aqiLabels[aqi - 1] || 'Unknown';

  const comfortIndex = Math.round(100 - Math.abs(temp - 22) * 2 - (humidity > 70 ? (humidity - 70) : 0));

  const alerts = [];
  if (temp > 35) alerts.push('üî• Extreme Heat Warning');
  if (temp < 0) alerts.push('‚ùÑÔ∏è Freezing Temperature Alert');
  if (windSpeed > 15) alerts.push('üí® High Wind Advisory');
  if (aqi >= 4) alerts.push('üò∑ Poor Air Quality Alert');
  if (weatherMain === 'Thunderstorm') alerts.push('‚õàÔ∏è Thunderstorm Warning');

  results.push({
    json: {
      city: current.name || 'Unknown',
      country: current.sys?.country || '',
      timestamp: new Date().toISOString(),
      current: {
        temperature: temp,
        feelsLike: current.main?.feels_like || temp,
        humidity: humidity,
        windSpeed: windSpeed,
        weather: weatherDesc,
        weatherMain: weatherMain
      },
      airQuality: {
        aqi: aqi,
        label: aqiLabel
      },
      analysis: {
        comfortIndex: Math.max(0, Math.min(100, comfortIndex)),
        alerts: alerts,
        hasAlerts: alerts.length > 0
      }
    }
  });
}

return results;

