const labels = [];
const data = [];
const colors = [];

const sentimentColors = {
  'Positive': '#4CAF50',
  'Negative': '#F44336',
  'Neutral': '#FFC107'
};

const sentimentCount = {};

items.forEach(item => {
  const sentiment = item.json.sentiment;

  if (sentimentCount[sentiment]) {
    sentimentCount[sentiment]++;
  } else {
    sentimentCount[sentiment] = 1;
  }
});

Object.keys(sentimentCount).forEach(sentiment => {
  labels.push(sentiment);
  data.push(sentimentCount[sentiment]);
  colors.push(sentimentColors[sentiment] || '#9E9E9E'); // colore di default se non trovato
});

const chartConfig = {
  type: "pie",
  data: {
    labels: labels,
    datasets: [{
      data: data,
      backgroundColor: colors
    }]
  },
  options: {
    plugins: {
      title: {
        display: true,
        text: "Sentiment"
      },
      legend: {
        position: "bottom"
      }
    }
  }
};

return [{
  json: {
    labels,
    data,
    colors,
    chartConfig: JSON.stringify(chartConfig)
  }
}];

const reviews = $input.all().map(item => item.json);

return [
  {
    json: {
      reviews,
    },
  },
];
