const items = $input.all();
const validatedItems = [];

for (const item of items) {
  const data = item.json;
  const errors = [];

  // FERPA-compliant validation - check for required fields
  if (!data.studentId) errors.push('Missing student ID');
  if (!data.studentEmail) errors.push('Missing student email');
  if (!data.courseId) errors.push('Missing course ID');
  if (!data.assignmentId) errors.push('Missing assignment ID');
  if (!data.submissionText && !data.submissionFile) errors.push('Missing submission content');

  // Validate email format
  const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
  if (data.studentEmail && !emailRegex.test(data.studentEmail)) {
    errors.push('Invalid email format');
  }

  validatedItems.push({
    json: {
      ...data,
      isValid: errors.length === 0,
      validationErrors: errors,
      validatedAt: new Date().toISOString()
    }
  });
}

return validatedItems;

const items = $input.all();
const config = $('Workflow Configuration').first().json;
const processedItems = [];

for (const item of items) {
  const data = item.json;
  const submittedAt = new Date(data.submittedAt);
  const deadline = new Date(data.deadline);

  let isLate = false;
  let daysLate = 0;
  let penaltyPercent = 0;
  let penaltyPoints = 0;

  if (submittedAt > deadline) {
    isLate = true;
    const diffMs = submittedAt - deadline;
    daysLate = Math.ceil(diffMs / (1000 * 60 * 60 * 24));
    penaltyPercent = Math.min(daysLate * config.latePenaltyPercent, 100);
  }

  processedItems.push({
    json: {
      ...data,
      isLate,
      daysLate,
      penaltyPercent,
      submissionStatus: isLate ? 'late' : 'on_time',
      processedAt: new Date().toISOString()
    }
  });
}

return processedItems;

