parameters.jsCode:
const processedIds = $getWorkflowStaticData('global').processedIds || new Set();
const newItems = [];

for (const item of $input.all()) {
  const txnId = item.json.transactionId;
  if (!processedIds.has(txnId)) {
    processedIds.add(txnId);
    newItems.push(item);
  }
}

$getWorkflowStaticData('global').processedIds = processedIds;

return newItems;

parameters.inputSchema:
{
	"type": "object",
	"properties": {
		"riskScore": {
			"type": "number",
			"description": "Numerical risk score for the transaction"
		},
		"riskLevel": {
			"type": "string",
			"enum": ["high", "medium", "low"],
			"description": "Risk level classification"
		},
		"fraudIndicators": {
			"type": "array",
			"items": {
				"type": "string"
			},
			"description": "List of fraud indicators detected"
		},
		"anomalyDetected": {
			"type": "boolean",
			"description": "Whether an anomaly was detected"
		},
		"complianceFlags": {
			"type": "array",
			"items": {
				"type": "string"
			},
			"description": "Compliance-related flags"
		},
		"recommendation": {
			"type": "string",
			"description": "Recommended action for this transaction"
		}
	},
	"required": ["riskScore", "riskLevel", "fraudIndicators", "anomalyDetected", "complianceFlags", "recommendation"]
}

