parameters.jsCode:
const transactions = $input.first().json.data;
const totalCount = transactions.length;
const totalAmount = transactions.reduce((sum, t) => sum + t.amount, 0);
const avgRiskScore = transactions.reduce((sum, t) => sum + t.riskScore, 0) / totalCount;
const highRisk = transactions.filter(t => t.riskLevel === 'high').length;
const mediumRisk = transactions.filter(t => t.riskLevel === 'medium').length;
const lowRisk = transactions.filter(t => t.riskLevel === 'low').length;
const report = `<h2>Financial Transaction Report</h2><p>Generated: ${new Date().toISOString()}</p><h3>Summary</h3><ul><li>Total Transactions: ${totalCount}</li><li>Total Amount: $${totalAmount.toFixed(2)}</li><li>Average Risk Score: ${avgRiskScore.toFixed(2)}</li><li>High Risk: ${highRisk}</li><li>Medium Risk: ${mediumRisk}</li><li>Low Risk: ${lowRisk}</li></ul>`;
return [{ json: { report, totalCount, totalAmount, avgRiskScore, highRisk, mediumRisk, lowRisk } }];

