=key != {{ $('Simplify Ticket').item.json.issueKey }}
AND status in ("Resolved", "Closed", "Done")
AND resolutiondate >= startOfMonth(-1)
AND labels in ({{
  $('Label, Prioritize & Rewrite').item.json.output.labels
  .map(label => `"${label}"`)
  .join(',')
}})

