=## Message
{{ $json.userName }} (<@{{ $json.userId }}>) says at {{ new DateTime(parseFloat($json.ts)*1000).format('d MMM HH:mma') }}:
> {{ $json.text }}

## {{ ($json.replies ?? []).compact().length }} Replies
{{
($json.replies ?? [])
  .compact()
    .map(reply => ({
      ...reply,
      userName: $('Reply Users').item.json.data
        .find(user => user.id === reply.userId)?.name
    }))
    .map(reply =>
      `* ${new DateTime(parseFloat($json.ts)*1000).format('d MMM HH:mma')}, ${reply.userName} (<@${reply.userId}>) replies: ${reply.text}`
)
    .join('\n')

}}

