assignments.value:
={{ 
  $('Aggregate Fees').item.json.data.map(fee => {
    const position = $('Aggregate Positions').item.json.data.find(pos => pos.symbol === fee.symbol) || {};
    const token = $('Aggregate Tokens').last().json.data.find(tok => tok.name === fee.symbol) || {};

    return {
      symbol: fee.symbol,
      income: fee.income,
      asset: fee.asset,
      time: fee.time,
      tranId: fee.tranId,
      positionAmt: position.positionAmt || "0",
      markPrice: position.markPrice || "0.00000000",
      liquidationPrice: position.liquidationPrice || "0",
      tokenId: token.id || null  // Get ID from Get Tokens, or null if not found
    };
  })
}}
