from datetime import datetime

all_news = _input.all()

valid_news = []
for item in all_news:
    news = item.json
    date_str = news.get("publication_date")
    try:
        date = datetime.strptime(date_str, "%Y-%m-%dT%H:%M:%S.%fZ")
        valid_news.append((date, news))
    except (TypeError, ValueError):
        continue

sorted_news = sorted(valid_news, key=lambda x: x[0], reverse=True)

cleaned = []
for _, c in sorted_news[:10]:
    topics = c.get("topics") or []

    cleaned.append({
        "headline": c.get("headline"),
        "url": c.get("url"),
        "author": c.get("author"),
        "publication_date": c.get("publication_date"),
        "type": c.get("type"),
        "content": c.get("content"),
        "keyword": c.get("keyword"),
        "topics": ", ".join(topics)
    })

return [{
    "json": {
        "news": cleaned
    }
}]

