// N8N Code Node to extract data from AI output
const items = $input.all();

return items.map(item => {
  try {
    // Get the output string from the item
    const outputString = item.json.output;

    // Extract JSON from the markdown code block
    const jsonMatch = outputString.match(/```json\n([\s\S]*?)\n```/);

    if (jsonMatch && jsonMatch[1]) {
      // Parse the extracted JSON
      const parsedData = JSON.parse(jsonMatch[1]);

      // Return the extracted data as separate columns
      return {
        json: {
          Collaboration: parsedData.Collaboration || '',
          Analysis: parsedData.Analysis || '',
          Profile_ID: parsedData['Profile ID'] || '',
          // Keep original data as well if needed
          original_output: outputString
        }
      };
    } else {
      // If no JSON found, return empty values
      return {
        json: {
          Collaboration: '',
          Analysis: '',
          Profile_ID: '',
          original_output: outputString,
          error: 'No JSON found in output'
        }
      };
    }
  } catch (error) {
    // Handle any parsing errors
    return {
      json: {
        Collaboration: '',
        Analysis: '',
        Profile_ID: '',
        original_output: item.json.output || '',
        error: `Parsing error: ${error.message}`
      }
    };
  }
});

