const results = [];

for (const item of items) {
  const articleUrl = item.json.link;
  const articleTitle = item.json.title;
  const articleId = item.json.id;
  const html = item.json.content;

  const imgRegex = /<img[^>]*src="([^"]+)"[^>]*alt="([^"]*)"?[^>]*>/g;
  let match;

  while ((match = imgRegex.exec(html)) !== null) {
    const imageUrl = match[1];
    const altText = match[2];

    results.push({
      json: {
        articleId: articleId,
        articleTitle: articleTitle,
        article_url: articleUrl,
        image_url: imageUrl,
        alt_text: altText
      }
    });
  }
}

return results;

const base64 = $binary["data"].data;
const fileName = $binary["data"].fileName || 'unknown.png';
const extension = fileName.split('.').pop().toLowerCase();
const fileSizeKB = $binary["data"].fileSize || 'unknown.png';
const size_ok = fileSizeKB <= 500 ? true : false;

return {
  json: {
    file_name: fileName,
    file_size_kb: fileSizeKB,
    format: extension,
    filename_seo: /[a-z0-9-]{3,}/.test(fileName) ? true : false,
    size_ok: size_ok
  }
};
