let requests = $("Download Document").all()

requests.forEach((request) => {
  let mime_type = request.json.mime_type

  if (
    mime_type === "text/calendar" || 
    mime_type === "application/ics" || 
    mime_type === "text/x-calendar"
  ) {
    request.json.mime_type = "mapped/calendar"
  }

  if (
    mime_type === "application/xml" || 
    mime_type === "text/xml") {
    request.json.mime_type = "mapped/xml"
  }

  if (!mime_type) {
    request.json.mime_type = $('Gets WhatsApp Document Source URL').first().json.mime_type
  }
})

return requests;

