parameters.inputSchema:
{
  "$schema": "http://json-schema.org/schema#",
  "title": "Salary Information",
  "type": "object",
  "properties": {
    "job_title": {
      "type": "string",
      "description": "The official title of the job (e.g., Software Engineer, DevOps Engineer)"
    },
    "job_level": {
      "type": "string",
      "description": "The seniority level (e.g., Junior, Mid, Senior, Lead, Principal)",
      "enum": ["Intern", "Junior", "Mid", "Senior", "Lead", "Principal", "Director", "VP", "C-Level"]
    },
    "company_name": {
      "type": "string",
      "description": "The name of the company offering the position"
    },
    "location": {
      "type": "string",
      "description": "City and state/country of the job"
    },
    "employment_type": {
      "type": "string",
      "description": "Full-time, Part-time, Contract, Freelance, Internship",
      "enum": ["Full-time", "Part-time", "Contract", "Freelance", "Internship"]
    },
    "remote_option": {
      "type": "string",
      "description": "Remote status of the job",
      "enum": ["On-site", "Hybrid", "Remote"]
    },
    "currency": {
      "type": "string",
      "description": "Currency code (e.g., USD, EUR, INR)"
    },
    "salary_min": {
      "type": "number",
      "description": "Minimum base salary offered"
    },
    "salary_max": {
      "type": "number",
      "description": "Maximum base salary offered"
    },
    "salary_period": {
      "type": "string",
      "description": "Salary frequency",
      "enum": ["hour", "day", "week", "month", "year"]
    },
    "equity": {
      "type": "string",
      "description": "Equity component of the compensation (e.g., '0.1%', '$5,000 in stock')",
      "nullable": true
    },
    "bonus": {
      "type": "string",
      "description": "Bonus component (e.g., '10% performance bonus', '$5000 sign-on')",
      "nullable": true
    },
    "skills_required": {
      "type": "array",
      "description": "Key technical or soft skills expected",
      "items": {
        "type": "string"
      }
    },
    "experience_required": {
      "type": "string",
      "description": "Experience required in years or description (e.g., '3-5 years', 'Entry level')"
    },
    "posting_source": {
      "type": "string",
      "description": "Source of the job posting (e.g., LinkedIn, Glassdoor, Indeed)"
    },
    "posting_date": {
      "type": "string",
      "format": "date",
      "description": "Date when the job was posted"
    },
    "summary_insight": {
      "type": "string",
      "description": "Optional Gemini-generated summary or insight (e.g., 'This role offers 15% higher pay than average for the region.')"
    }
  }
}

parameters.functionCode:
items[0].binary = {
  data: {
    data: new Buffer(JSON.stringify(items[0].json, null, 2)).toString('base64')
  }
};
return items;
