parameters.jsCode:
const results = [];

for (const item of items) {
  const companyIndustry = $('Set Fields').first().json['Company Industry']; 

  let trelloIndustryOptionId = null;

  switch (companyIndustry) {
    case 'Consumer Goods':
      trelloIndustryOptionId = '{option-id}';
      break;
    case 'Education':
      trelloIndustryOptionId = '{option-id}';
      break;
    case 'Energy & Utilities':
      trelloIndustryOptionId = '{option-id}';
      break;
    case 'Entertainment & Media':
      trelloIndustryOptionId = '{option-id}';
      break;
    case 'Financial Services':
      trelloIndustryOptionId = '{option-id}';
      break;
    case 'Healthcare & Medical':
      trelloIndustryOptionId = '{option-id}';
      break;
    case 'Hospitality & Travel':
      trelloIndustryOptionId = '{option-id}';
      break;
    case 'Information Technology (IT) / Software':
      trelloIndustryOptionId = '{option-id}';
      break;
    case 'Legal Services':
      trelloIndustryOptionId = '{option-id}';
      break;
    case 'Marketing & Advertising':
      trelloIndustryOptionId = '{option-id}';
      break;
    case 'Professional Services / Consulting':
      trelloIndustryOptionId = '{option-id}';
      break;
    case 'Real Estate':
      trelloIndustryOptionId = '{option-id}';
      break;
    case 'Retail / E-commerce':
      trelloIndustryOptionId = '{option-id}';
      break;
    case 'Other':
      trelloIndustryOptionId = '{option-id}';
      break;
    default:
      console.warn(`No matching Trello option found for industry: ${companyIndustry}`);
      break;
  }

  results.push({
    json: {

      companyIndustry: companyIndustry, 
      trelloIndustryOptionId: trelloIndustryOptionId, 
    },

    pairedItem: item.pairedItem,
  });
}


return results;
