const email = String($json.Email || '');
const regex_email = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
const mobile = String($json.Mobile || '');
const regex_mobile = /^(\+\d{1,3}[- ]?)?\d{10}$/;

let validEmail = false;

if (!email) {
  validEmail = true;  // Treat empty as valid
} else if (regex_email.test(email)) {
  validEmail = true;  // Matches regex
} else {
  validEmail = false; // Invalid format
}

let validMobile = false;

if (!mobile) {
  validMobile = true;  // Treat empty as valid
} else if (regex_mobile.test(mobile)) {
  validMobile = true;  // Matches regex
} else {
  validMobile = false; // Invalid format
}

return {
  json: {
    ...$json,
    validEmail: validEmail,
    validMobile:validMobile
  }
};

const mobileRaw = String($json.Mobile || '').trim();

let mobile_country_code = '';
let mobile = '';

if (mobileRaw) {
  if (mobileRaw.includes('-')) {
    // Split by -
    const parts = mobileRaw.split('-').map(p => p.trim());

    if (parts.length >= 2) {
      mobile_country_code = parts[0];
      mobile = parts.slice(1).join(' ');

      // Ensure country code starts with +
      if (!mobile_country_code.startsWith('+')) {
        mobile_country_code = '+' + mobile_country_code;
      }
    }
  } else {
    // No dash, treat as mobile
    mobile = mobileRaw;
  }
}

return {
  json: {
    mobile_country_code,
    mobile
  }
};

const date = new Date();
const formatted = date.getDate().toString().padStart(2, '0') + '-' +
  (date.getMonth() + 1).toString().padStart(2, '0') + '-' +
  date.getFullYear() + ' ' +
  date.getHours().toString().padStart(2, '0') + ':' +
  date.getMinutes().toString().padStart(2, '0') + ':' +
  date.getSeconds().toString().padStart(2, '0');

return {
  json: {
    ...$json,
    created_at: formatted
  }
};
