const emails = $input.all().map((item) => item.json);

const extractedData = emails.map((email) => {
  const emailContent = email.textPlain;
  const emailSubject = email.subject;
  const emailDate = email.date;
  const senderEmail = email.from;
  const senderName = email.from.split("@")[0];// Assuming the name is the part before the '@' in the email

  return {
    ID: email.attributes.uid,
    Date: emailDate,
    Subject: emailSubject,
    EmailContent: emailContent,
    SenderEmail: senderEmail,
    SenderName: senderName,

  };
});

return extractedData;
