// Function to recursively flatten nested objects
function flattenObject(obj, prefix = '') {
  let flatObject = {};

  for (let key in obj) {
    if (typeof obj[key] === 'object' && obj[key] !== null) {
      // Recursively flatten nested objects
      Object.assign(flatObject, flattenObject(obj[key], prefix + key + '.'));
    } else {
      flatObject[prefix + key] = obj[key];
    }
  }

  return flatObject;
}

// Function to transform nested JSON into a flat table structure
function flattenToTable(inputData) {
  if (!Array.isArray(inputData)) {
    throw new Error('Invalid input data. Expected an array.');
  }

  // Result array for table rows
  const tableRows = [];

  // Loop over each item in the input data
  inputData.forEach((item, index) => {
    // Recursively flatten each data entry
    let flatRow = flattenObject(item);

    // Remove the "json.data.0." prefix from each key
    let cleanedRow = {};
    for (let key in flatRow) {
      let newKey = key.replace(/^json\.data\.0\./, '');  // Strip the prefix
      cleanedRow[newKey] = flatRow[key];
    }

    tableRows.push(cleanedRow);
  });

  console.log('Final Table Rows:', tableRows); // Debugging final table rows
  return tableRows;
}

