try {
  // 1. Get raw output from the input JSON
  const rawOutput = $input.first().json.output || "";

  // 2. Extract JSON from code fences, if present (supports json, html, or other language tags)
  const jsonMatch = rawOutput.match(/```(?:json|html|[a-zA-Z]*)\s*([\s\S]*?)\s*```/);
  let cleanedOutput = jsonMatch ? jsonMatch[1] : rawOutput;

  // 3. Remove potential HTML wrappers like DOCTYPE, <html>, <head>, <body> tags
  cleanedOutput = cleanedOutput.replace(
    /<!DOCTYPE[^>]*>|<html[^>]*>|<\/html>|<head[^>]*>[\s\S]*?<\/head>|<body[^>]*>|<\/body>/gi,
    ''
  );

  // 4. Remove potential BOM (byte order mark) and trim whitespace
  cleanedOutput = cleanedOutput.replace(/^\uFEFF/, '').trim();

  // 5. Sanitize control characters while preserving newlines (\n), carriage returns (\r), and tabs (\t)
  cleanedOutput = cleanedOutput.replace(/[\u0000-\u001F\u007F]/g, (char) => {
    return (char === '\n' || char === '\r' || char === '\t') ? char : ' ';
  });

  // 6. Additional sanitization: flatten HTML content within JSON string fields
  //    Remove spaces between tags and collapse multiple spaces into one.
  cleanedOutput = cleanedOutput.replace(/>\s+</g, '><').replace(/\s+/g, ' ').trim();

  // 7. Try to parse the JSON. If it fails, attempt further sanitization by escaping unescaped double quotes in HTML text
  let parsedData;
  try {
    parsedData = JSON.parse(cleanedOutput);
  } catch (parseError) {
    // Fallback: Escape all double quotes that occur between a '>' and a '<'
    const furtherSanitizedOutput = cleanedOutput.replace(/>([^<]+)</g, (match, p1) => {
      // Replace all double quotes in the text content with escaped ones
      return '>' + p1.replace(/"/g, '\\"') + '<';
    });
    parsedData = JSON.parse(furtherSanitizedOutput);
  }

  // 8. Function to convert HTML content fields to a single-line format
  function convertHtmlToSingleLine(data) {
    if (typeof data === 'string') {
      return data
        .replace(/[\n\r]+/g, ' ')  // Remove all newline and carriage return characters
        .replace(/\s+/g, ' ')      // Collapse multiple spaces into one
        .trim();
    } else if (Array.isArray(data)) {
      return data.map(item => convertHtmlToSingleLine(item));
    } else if (typeof data === 'object' && data !== null) {
      const newObj = {};
      for (const key in data) {
        if (data.hasOwnProperty(key)) {
          newObj[key] = convertHtmlToSingleLine(data[key]);
        }
      }
      return newObj;
    }
    return data;
  }

  // 9. Apply the HTML conversion to the parsed data
  parsedData = convertHtmlToSingleLine(parsedData);

  // 10. Dynamically format the output:
  //     If parsedData is an array, return it; if it‚Äôs an object, wrap it in an array.
  let formattedOutput;
  if (Array.isArray(parsedData)) {
    formattedOutput = parsedData.map(item => {
      const dynamicObject = {};
      for (const key in item) {
        if (item.hasOwnProperty(key)) {
          dynamicObject[key] = item[key];
        }
      }
      return dynamicObject;
    });
  } else if (typeof parsedData === 'object' && parsedData !== null) {
    const dynamicObject = {};
    for (const key in parsedData) {
      if (parsedData.hasOwnProperty(key)) {
        dynamicObject[key] = parsedData[key];
      }
    }
    formattedOutput = [dynamicObject];
  } else {
    throw new Error("Unsupported JSON format. Expected an array or object.");
  }

  // 11. Return the formatted output
  return formattedOutput;

} catch (error) {
  throw new Error("Failed to parse JSON output: " + error.message);
}
