// Get the output string and page_id from the previous node
const outputText = $input.item.json.output;
const pageId = $input.item.json.page_id; // Add this if you have the page ID in your data

// Function to convert text to Notion blocks
function textToNotionBlocks(text) {
  if (!text || typeof text !== 'string') {
    return [];
  }

  const blocks = [];
  const lines = text.split('\n');
  let inCodeBlock = false;
  let codeBlockContent = [];
  let codeBlockLanguage = '';

  for (let i = 0; i < lines.length; i++) {
    const line = lines[i];
    const trimmedLine = line.trim();

    // Handle code blocks
    if (trimmedLine.startsWith('```')) {
      if (!inCodeBlock) {
        // Starting a code block
        inCodeBlock = true;
        codeBlockLanguage = trimmedLine.substring(3).trim();
        codeBlockContent = [];
      } else {
        // Ending a code block
        inCodeBlock = false;
        blocks.push({
          object: 'block',
          type: 'code',
          code: {
            caption: [],
            rich_text: [{
              type: 'text',
              text: { content: codeBlockContent.join('\n') }
            }],
            language: codeBlockLanguage || 'plain text'
          }
        });
        codeBlockContent = [];
        codeBlockLanguage = '';
      }
      continue;
    }

    // If we're inside a code block, collect the content
    if (inCodeBlock) {
      codeBlockContent.push(line);
      continue;
    }

    // Skip empty lines
    if (!trimmedLine) {
      continue;
    }

    // Check for horizontal rules
    if (trimmedLine === '---' || trimmedLine === '***' || trimmedLine === '___') {
      blocks.push({
        object: 'block',
        type: 'divider',
        divider: {}
      });
    }
    // Check for headers (markdown style)
    else if (trimmedLine.startsWith('#### ')) {
      const richText = parseRichText(trimmedLine.substring(5));
      blocks.push({
        object: 'block',
        type: 'heading_3',
        heading_3: {
          rich_text: richText
        }
      });
    }
    else if (trimmedLine.startsWith('### ')) {
      const richText = parseRichText(trimmedLine.substring(4));
      blocks.push({
        object: 'block',
        type: 'heading_3',
        heading_3: {
          rich_text: richText
        }
      });
    }
    else if (trimmedLine.startsWith('## ')) {
      const richText = parseRichText(trimmedLine.substring(3));
      blocks.push({
        object: 'block',
        type: 'heading_2',
        heading_2: {
          rich_text: richText
        }
      });
    }
    else if (trimmedLine.startsWith('# ')) {
      const richText = parseRichText(trimmedLine.substring(2));
      blocks.push({
        object: 'block',
        type: 'heading_1',
        heading_1: {
          rich_text: richText
        }
      });
    }
    // Check for bullet points
    else if (trimmedLine.startsWith('- ') || trimmedLine.startsWith('* ')) {
      const richText = parseRichText(trimmedLine.substring(2));
      blocks.push({
        object: 'block',
        type: 'bulleted_list_item',
        bulleted_list_item: {
          rich_text: richText
        }
      });
    }
    // Check for numbered lists
    else if (/^\d+\.\s/.test(trimmedLine)) {
      const content = trimmedLine.replace(/^\d+\.\s/, '');
      const richText = parseRichText(content);
      blocks.push({
        object: 'block',
        type: 'numbered_list_item',
        numbered_list_item: {
          rich_text: richText
        }
      });
    }
    // Check for quotes
    else if (trimmedLine.startsWith('> ')) {
      const richText = parseRichText(trimmedLine.substring(2));
      blocks.push({
        object: 'block',
        type: 'quote',
        quote: {
          rich_text: richText
        }
      });
    }
    // Regular paragraph
    else {
      const richText = parseRichText(trimmedLine);
      blocks.push({
        object: 'block',
        type: 'paragraph',
        paragraph: {
          rich_text: richText
        }
      });
    }
  }

  // Handle case where code block wasn't closed
  if (inCodeBlock && codeBlockContent.length > 0) {
    blocks.push({
      object: 'block',
      type: 'code',
      code: {
        caption: [],
        rich_text: [{
          type: 'text',
          text: { content: codeBlockContent.join('\n') }
        }],
        language: codeBlockLanguage || 'plain text'
      }
    });
  }

  return blocks;
}

// Function to parse rich text formatting (bold, italic, links)
function parseRichText(text) {
  const richTextArray = [];

  // Handle simple case with no formatting
  if (!text.includes('**') && !text.includes('__') && !text.includes('*') && 
      !text.includes('_') && !text.includes('[')) {
    return [{
      type: 'text',
      text: { content: text }
    }];
  }

  let workingText = text;
  let segments = [];

  // Process links first [text](url)
  const linkRegex = /\[([^\]]*)\]\(([^)]*)\)/g;
  let linkMatch;
  let lastLinkIndex = 0;

  while ((linkMatch = linkRegex.exec(text)) !== null) {
    // Add text before link
    if (linkMatch.index > lastLinkIndex) {
      const beforeText = text.substring(lastLinkIndex, linkMatch.index);
      if (beforeText) {
        segments.push({ type: 'text', content: beforeText });
      }
    }

    // Add link
    segments.push({
      type: 'link',
      content: linkMatch[1],
      url: linkMatch[2]
    });

    lastLinkIndex = linkMatch.index + linkMatch[0].length;
  }

  // Add remaining text after last link
  if (lastLinkIndex < text.length) {
    const remainingText = text.substring(lastLinkIndex);
    if (remainingText) {
      segments.push({ type: 'text', content: remainingText });
    }
  }

  // If no links found, treat entire text as one segment
  if (segments.length === 0) {
    segments.push({ type: 'text', content: text });
  }

  // Now process each segment for bold/italic formatting
  segments.forEach(segment => {
    if (segment.type === 'link') {
      // Links can have formatting too
      const formattedContent = processTextFormatting(segment.content);
      formattedContent.forEach(item => {
        richTextArray.push({
          type: 'text',
          text: { 
            content: item.content,
            link: { url: segment.url }
          },
          annotations: item.annotations || {}
        });
      });
    } else {
      // Regular text - process for formatting
      const formattedContent = processTextFormatting(segment.content);
      formattedContent.forEach(item => {
        richTextArray.push({
          type: 'text',
          text: { content: item.content },
          annotations: item.annotations || {}
        });
      });
    }
  });

  return richTextArray;
}

// Helper function to process bold/italic formatting
function processTextFormatting(text) {
  const result = [];
  let currentIndex = 0;

  // Patterns for markdown formatting - order matters (bold before italic)
  const patterns = [
    { regex: /\*\*(.*?)\*\*/g, annotation: { bold: true } },
    { regex: /__(.*?)__/g, annotation: { bold: true } },
    { regex: /\*(.*?)\*/g, annotation: { italic: true } },
    { regex: /_(.*?)_/g, annotation: { italic: true } }
  ];

  let matches = [];

  // Find all formatting matches
  patterns.forEach(pattern => {
    let match;
    const regex = new RegExp(pattern.regex.source, pattern.regex.flags);
    while ((match = regex.exec(text)) !== null) {
      matches.push({
        start: match.index,
        end: match.index + match[0].length,
        content: match[1],
        annotation: pattern.annotation,
        fullMatch: match[0]
      });
    }
  });

  // Sort matches by position and remove overlapping matches
  matches.sort((a, b) => a.start - b.start);

  // Remove overlapping matches (keep the first one)
  const filteredMatches = [];
  let lastEnd = -1;

  matches.forEach(match => {
    if (match.start >= lastEnd) {
      filteredMatches.push(match);
      lastEnd = match.end;
    }
  });

  // If no formatting found, return simple text
  if (filteredMatches.length === 0) {
    return [{ content: text }];
  }

  // Build result array
  let lastIndex = 0;

  filteredMatches.forEach(match => {
    // Add text before the match
    if (match.start > lastIndex) {
      const beforeText = text.substring(lastIndex, match.start);
      if (beforeText) {
        result.push({ content: beforeText });
      }
    }

    // Add the formatted text
    result.push({
      content: match.content,
      annotations: match.annotation
    });

    lastIndex = match.end;
  });

  // Add remaining text
  if (lastIndex < text.length) {
    const remainingText = text.substring(lastIndex);
    if (remainingText) {
      result.push({ content: remainingText });
    }
  }

  return result;
}

// Convert the output text to Notion blocks
const notionBlocks = textToNotionBlocks(outputText);

// Create plain text excerpt (first 300 characters)
function createPlainTextExcerpt(text, maxLength = 300) {
  if (!text || typeof text !== 'string') {
    return '';
  }

  // Remove markdown formatting for plain text excerpt
  let plainText = text
    // Remove headers
    .replace(/^#{1,6}\s+/gm, '')
    // Remove horizontal rules
    .replace(/^[-*_]{3,}$/gm, '')
    // Remove code blocks
    .replace(/```[\s\S]*?```/g, '')
    // Remove inline code
    .replace(/`([^`]+)`/g, '$1')
    // Remove links but keep text
    .replace(/\[([^\]]*)\]\([^)]*\)/g, '$1')
    // Remove bold and italic
    .replace(/\*\*([^*]+)\*\*/g, '$1')
    .replace(/__([^_]+)__/g, '$1')
    .replace(/\*([^*]+)\*/g, '$1')
    .replace(/_([^_]+)_/g, '$1')
    // Remove bullet points and list markers
    .replace(/^[-*+]\s+/gm, '')
    .replace(/^\d+\.\s+/gm, '')
    // Remove quotes
    .replace(/^>\s+/gm, '')
    // Clean up extra whitespace
    .replace(/\n\s*\n/g, '\n')
    .replace(/^\s+|\s+$/g, '')
    .replace(/\s+/g, ' ');

  // Truncate to maxLength and add ellipsis if needed
  if (plainText.length > maxLength) {
    // Try to break at a word boundary
    const truncated = plainText.substring(0, maxLength);
    const lastSpace = truncated.lastIndexOf(' ');

    if (lastSpace > maxLength * 0.8) { // If we can break at a word within 80% of max length
      return truncated.substring(0, lastSpace) + '...';
    } else {
      return truncated + '...';
    }
  }

  return plainText;
}

const plainTextExcerpt = createPlainTextExcerpt(outputText);

// Return the blocks in the format expected by n8n
return {
  json: {
    blocks: notionBlocks,
    page_id: pageId, // Include page_id if available
    original_output: outputText,
    plain_text_excerpt: plainTextExcerpt,
    block_count: notionBlocks.length
  }
};
