const urlRegex = /(https?:\/\/[^\s]+)/g;
const text = $input.first().json.message.reply_to_message.text;
const urls = text.match(urlRegex);

if (!urls || urls.length === 0) {
    return {
        json: {
            error: "There is not detected URL. Please input valid URL that start with http:// or https://",
            chat_id: $input.item.json.chat_id // Take chat_id for error message
        }
    };
}

const longUrl = urls[0]; // Take first URL appear

// Function for generate random URL
function generateRandomCode(length = 7) {
    const characters = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
    let result = '';
    for (let i = 0; i < length; i++) {
        result += characters.charAt(Math.floor(Math.random() * characters.length));
    }
    return result;
}

let shortCode = generateRandomCode();

// Prepare data for MongoDB
return {
    json: {
        longUrl: longUrl,
        shortCode: shortCode,
        chat_id: $input.item.json.chat_id,
        message_id: $input.item.json.message_id
    }
};

