const variables = {
  // https://apify.com/apify/instagram-scraper 
  // Instagram Scraper = apify/instagram-scraper, 
  "scrapingActorId" : "shu8hvrXbJbY3Eb9W", // ID of Apify scraper
  "daysLimit" : 7, // Date limit 
  "resultsLimit" : 3, // Results per account
  "maxDays" : 2, // Sleeping || Active
  "translationLang" : "German",
}


return [{json : variables}]

// required Simplify = true

const query = {
    "addParentData": false,
    "directUrls": [],
    "enhanceUserSearchWithFacebookPage": false,
    "isUserReelFeedURL": false,
    "isUserTaggedFeedURL": false,
    "onlyPostsNewerThan": ($('Variables').first().json.daysLimit ?? 10) + ' days',
    "resultsLimit": $('Variables').first().json.resultsLimit ?? 5,
    "resultsType": "stories"
};

const urls = [];
const map = {};

items.forEach(item => {
  const username = item.json.property_username || '';
  if (username) {
    urls.push('https://www.instagram.com/' + username + '/');
    map[username.toLowerCase()] = item.json.id;   // Notion pageId
  }
});

query.directUrls = urls;

return [
  {
    json: {
      query,
      urls,
      map     
    }
  }
];

const accountsMap = {};

items.forEach(item => {
  const username = item.json.property_username || '';
  if (username) {
    accountsMap[username.toLowerCase()] = item.json.id;   // Notion pageId
  }
});

return [
  {
    json: {
      accountsMap,
      items
    }
  }
];

const existingPages = {};
for (const page of $items('Get Reels')) {
  const source = page.json.property_url ?? '';
  if (source) existingPages[source] = page.json;
}

const output = [];
const accountsMap = $('Many to One').first().json.accountsMap;

for (const reel of $items('Map Reels')) {

  const source   = reel.json.url;          

  const views = reel.json.videoViewCount;
  var update = {
    "Code": reel.json.shortCode,
    "SignedCode": reel.json.shortCode + '-' + Math.random(),
    "IsCreated" : false,
    "notionAccountPageId" : accountsMap[ reel.json.ownerUsername.toLowerCase() ],
          "Views"       : views ? views : reel.json.videoPlayCount,
          "Likes"       : reel.json.likesCount,
          "Comments"    : reel.json.commentsCount,
          "Saves"       : reel.json.savesCount ?? 0,
          "Shares"      : reel.json.sharesCount ?? 0,
          "Updated"   : new Date().toISOString()
  };

  /* ------------------------------------------------- */
  if (existingPages[source]) {

    update.notionPageId = existingPages[source].id;

  } else {
    update['IsCreated'] = true;
    update['Caption'] = reel.json.caption;
    update['URL'] = reel.json.url;
    update['Hashtags'] = reel.json.hashtags.join(', ');
    update['Duration'] = reel.json.videoDuration;
    update['Date'] = reel.json.timestamp;
    update['videoUrl'] = reel.json.videoUrl;

  }

  output.push({json: update});
}

return output;   // n8n continues with one array containing both kinds of items

const maxDays = $('Variables').first()?.json?.maxDays ?? 3;  
const now     = new Date();


var results = items;                             
const map   = $('Apify Payload').first().json.map;


const acctStatus = {};   // { username: 'Active' | 'Sleeping' }

for (const itm of results) {
  const owner = itm.json.ownerUsername.toLowerCase();
  const ts    = new Date(itm.json.timestamp);
  const diff  = (now - ts) / 86_400_000;    


  if (!acctStatus[owner] || acctStatus[owner] === 'Sleeping') {
    acctStatus[owner] = diff <= maxDays ? 'Active' : 'Sleeping';
  }
}

for (const k in results) {
  const item   = results[k];
  const owner  = item.json.ownerUsername.toLowerCase();

  // coauthorProducers - !notionPageId
  item.json.notionPageId     = map[owner];
  item.json.publishingStatus = acctStatus[owner];

  results[k] = item;
}

return results;

