const rawText = $input.first().json.candidates[0].content.parts[0].text;

const cleaned = rawText
  .replace(/^```json\s*/, '')      
  .replace(/\s*```$/, '')          
  .trim();

let parsed;
try {
  parsed = JSON.parse(cleaned);
} catch (e) {
  return [{ json: { error: 'Invalid JSON', message: e.message } }];
}

let item = $('Is Created?').first().json;

parsed.transcription = parsed.transcription ?? '';
parsed.hook = parsed.hook ?? '';
parsed.category = parsed.category ?? '';
parsed.format = parsed.format ?? '';
parsed.translation = parsed.translation ?? '';
parsed.translation_hook = parsed.translation_hook ?? '';

item.response = parsed;


return [{ json: item }];

// Loop over input items and add a new field called 'myNewField' to the JSON of each one
for (const item of $input.all()) {
  item.json.myNewField = 1;
}

return $input.all();
