// Get the raw output string from the previous node.
const rawOutput = $input.first().json.output;

// The AI often wraps JSON in Markdown code blocks (```json ... ```).
// We need to extract the pure JSON string from inside the fences.
// This regex will find the content between the fences. If no fences are found,
// it will fall back to using the entire rawOutput string.
const jsonStringMatch = rawOutput.match(/```json\s*([\s\S]*?)\s*```/);
const jsonString = jsonStringMatch ? jsonStringMatch[1] : rawOutput.trim();

// Now, try to parse the *cleaned* string.
try {
  const alerts = JSON.parse(jsonString);

  // Check if the result is a non-empty array.
  if (Array.isArray(alerts) && alerts.length > 0) {
    // Success! Pass the alerts to the next node.
    return [{ json: { alerts } }];
  }
} catch (e) {
  // This will catch errors if the cleaned string is still not valid JSON.
  console.error("NixGuard did not return a valid JSON array even after cleaning:", e);
  console.error("String that failed to parse:", jsonString);
}

// If parsing fails, it's not an array, or it's empty, return no items.
// This will correctly route the workflow to the 'false' branch of the IF node.
return [];

try {
  const alerts = $input.first().json.output;

  // Check if the result is a non-empty array.
  if (Array.isArray(alerts) && alerts.length > 0) {
    // Success! Return a separate item for each alert.
    // The .map() function transforms the array of alert objects
    // into an array of n8n items, which splits the execution.
    return alerts.map(alert => ({ json: alert }));
  }
} catch (e) {
  // This will catch errors if the cleaned string is still not valid JSON.
  console.error("NixGuard did not return a valid JSON array even after cleaning:", e);
  console.error("String that failed to parse:", jsonString);
}

// If parsing fails, it's not an array, or it's empty, return no items.
// This will correctly stop this branch of the workflow.
return [];

// Get the AI response. Your previous node used a complex path,
// we assume the final AI string is here. Adjust if needed.
// For example, it might be in $input.first().json.output or similar.
const rawOutput = $input.first().json.output;

const jsonString = rawOutput;

try {
  // Parse the cleaned JSON string
  const parsedResponse = JSON.parse(jsonString);
  // Add the parsed fields to the n8n item.
  // This merges the new data with any existing data from the input.
  return [{ 
    json: {
      ...$input.first().json,
      ...parsedResponse 
    }
  }];

} catch (e) {
  console.error("Failed to parse AI JSON response:", e);
  console.error("String that failed to parse:", jsonString);
  // Return an empty item to indicate failure and stop the flow if desired.
  return [];
}
