const allItems = $input.all();
const linkCount = {};

// ÁªüËÆ°ÊØè‰∏™ link ÁöÑÂá∫Áé∞Ê¨°Êï∞
allItems.forEach(item => {
  const link = item.json.link;
  linkCount[link] = (linkCount[link] || 0) + 1;
});

// ËøáÊª§Âè™Âá∫Áé∞ 1 Ê¨°ÁöÑËÆ∞ÂΩï
const uniqueItems = allItems.filter(item => {
  const link = item.json.link;
  return linkCount[link] === 1;
}).map(item => item.json);

return uniqueItems;

const items = $input.all();

// ÂàõÂª∫‰∏Ä‰∏™ÂØπË±°Êù•Ë∑üË∏™ÈìæÊé•Âá∫Áé∞Ê¨°Êï∞ÂíåÂØπÂ∫îÈ°π
const linkTracker = {};

// Á¨¨‰∏ÄÊ¨°ÈÅçÂéÜÔºöÁªüËÆ°ÊØè‰∏™ÈìæÊé•Âá∫Áé∞ÁöÑÊ¨°Êï∞ÂíåËÆ∞ÂΩïÂØπÂ∫îÈ°π
items.forEach(item => {
  const link = item.json.link ? item.json.link.trim().toLowerCase() : "";
  if (link) {
    if (!linkTracker[link]) {
      linkTracker[link] = {
        count: 0,
        items: []
      };
    }
    linkTracker[link].count++;
    linkTracker[link].items.push(item);
  }
});

console.log("Link Tracker:", JSON.stringify(linkTracker, null, 2));

// Á¨¨‰∫åÊ¨°ÈÅçÂéÜÔºöÁ≠õÈÄâÂá∫ÈáçÂ§çÈìæÊé•‰∏îÂ∏¶Êúâ content ÁöÑÈ°π
const result = [];
const processedLinks = new Set(); // Áî®‰∫éÈÅøÂÖçÈáçÂ§çÂ§ÑÁêÜÂêå‰∏Ä‰∏™ÈìæÊé•

for (const [link, data] of Object.entries(linkTracker)) {
  if (data.count > 1 && !processedLinks.has(link)) {
    processedLinks.add(link);
    const itemsWithContent = data.items.filter(item => item.json.content !== undefined && item.json.content !== "");
    if (itemsWithContent.length > 0) {
      result.push(...itemsWithContent);
    }
  }
}

console.log("Result:", JSON.stringify(result, null, 2));
return result;

const items = $input.all().map(item => item.json);

const cleanedData = items.map(item => {
  let text = "";

  // Â∞ùËØï‰ªé meta description ÊèêÂèñ
  const metaDescriptionMatch = item.content.match(/<meta\s+name=["']description["']\s+content=["'](.*?)["']/i);
  if (metaDescriptionMatch && metaDescriptionMatch[1]) {
    text = metaDescriptionMatch[1].trim();
  } else {
    // ÁßªÈô§ËÑöÊú¨ÂíåÊ†∑ÂºèÔºåÊèêÂèñÂèØËÉΩÊ≠£Êñá
    text = item.content
      .replace(/<script[^>]*>[\s\S]*?<\/script>/gi, "") // ÁßªÈô§ËÑöÊú¨
      .replace(/<style[^>]*>[\s\S]*?<\/style>/gi, "") // ÁßªÈô§Ê†∑Âºè
      .replace(/<!--[\s\S]*?-->/g, "") // ÁßªÈô§Ê≥®Èáä
      .replace(/<[^>]+>/g, " ") // ÁßªÈô§Ê†áÁ≠æ
      .replace(/window\..*?(?=\s|$)/g, "") // ÁßªÈô§ window ÂØπË±°
      .replace(/[\s\n\r]+/g, " ") // ÂêàÂπ∂Â§ö‰ΩôÁ©∫Ê†º
      .trim();
  }

  // Â¶ÇÊûú‰ªç‰∏∫Á©∫ÔºåÊ†áËÆ∞‰∏∫Êó†ÂÜÖÂÆπ
  if (!text) {
    text = "No content extracted";
  }

  return {
    ...item,
    cleanedContent: text
  };
});

console.log("Cleaned Data:", JSON.stringify(cleanedData, null, 2));
return cleanedData;
