const categories = $input.first().json.lighthouseResult?.categories || {};

function extractScore(cat) {
  return categories[cat]?.score !== undefined
    ? Math.round(categories[cat].score * 100)
    : "none";
}

return [{
  performance: extractScore("performance"),
  accessibility: extractScore("accessibility"),
  bestPractices: extractScore("best-practices"),
  seo: extractScore("seo"),
}];
