// This Function node takes input like your provided JSON
// and returns each backlink as a separate item

// 1Ô∏è‚É£ Get the input
const input = items[0].json.output;

// 2Ô∏è‚É£ Extract backlinks array
const backlinks = input.backlinks;

// 3Ô∏è‚É£ Build output items
const output = backlinks.map(backlink => {
  return {
    json: {
      domain: input.domain,
      title: backlink.title,
      url: backlink.url,
      category: backlink.category,
      date: backlink.date
    }
  };
});

// 4Ô∏è‚É£ Return array of individual items
return output;

