parameters.value:
={
    "type": "object",
    "properties": {
        "news": {
            "type": "array",
            "description": "A list of news articles",
            "items": {
                "type": "object",
                "properties": {
                    "title": {
                        "type": "string",
                        "description": "Title of the news article"
                    },
                    "url": {
                        "type": "string",
                        "description": "URL of the news article"
                    },
                    "content": {
                        "type": "string",
                        "description": "A summary of the news in a few sentences"
                    },
                    "date": {
                        "type": "string",
                        "format": "date-time",
                        "description": "Publication date of the news article in ISO 8601 format (e.g., 2025-07-18T14:30:00Z) - really extract the publication date of the article, not a specific date mentioned in the news content"
                    }
                },
                "required": ["title", "url", "content", "date"]
            }
        }
    },
    "required": ["news"]
}
