// Combine forum and news data for sentiment analysis
const forumData = $input.all()[0]?.json?.result?.discussions || [];
const newsData = $input.all()[1]?.json?.result?.news_articles || [];

function analyzeSentiment(text) {
  const bullishKeywords = ['buy', 'opportunity', 'growth', 'investment', 'profit', 'rising', 'bullish', 'positive', 'gain', 'uptrend'];
  const bearishKeywords = ['sell', 'crash', 'decline', 'loss', 'falling', 'bearish', 'negative', 'risk', 'downturn', 'bubble'];

  const textLower = text.toLowerCase();
  const bullishCount = bullishKeywords.filter(word => textLower.includes(word)).length;
  const bearishCount = bearishKeywords.filter(word => textLower.includes(word)).length;

  if (bullishCount > bearishCount) return 'bullish';
  if (bearishCount > bullishCount) return 'bearish';
  return 'neutral';
}

function calculateSentimentScore(sentiment) {
  switch(sentiment) {
    case 'bullish': return 1;
    case 'bearish': return -1;
    default: return 0;
  }
}

const forumSentiments = forumData.map(discussion => {
  const sentiment = analyzeSentiment(discussion.title + ' ' + discussion.content);
  return {
    source: 'forum',
    title: discussion.title,
    sentiment: sentiment,
    score: calculateSentimentScore(sentiment),
    url: discussion.url,
    date: discussion.date
  };
});

const newsSentiments = newsData.map(article => {
  const sentiment = analyzeSentiment(article.headline + ' ' + article.summary);
  return {
    source: 'news',
    title: article.headline,
    sentiment: sentiment,
    score: calculateSentimentScore(sentiment),
    url: article.url,
    date: article.date,
    market_impact: article.market_impact
  };
});

const allSentiments = [...forumSentiments, ...newsSentiments];
const totalScore = allSentiments.reduce((sum, item) => sum + item.score, 0);
const averageScore = totalScore / allSentiments.length || 0;

let overallSentiment = 'neutral';
if (averageScore > 0.3) overallSentiment = 'bullish';
else if (averageScore < -0.3) overallSentiment = 'bearish';

return [{
  json: {
    overall_sentiment: overallSentiment,
    sentiment_score: averageScore,
    total_sources: allSentiments.length,
    bullish_count: allSentiments.filter(s => s.sentiment === 'bullish').length,
    bearish_count: allSentiments.filter(s => s.sentiment === 'bearish').length,
    neutral_count: allSentiments.filter(s => s.sentiment === 'neutral').length,
    detailed_analysis: allSentiments,
    analysis_timestamp: new Date().toISOString()
  }
}];

// Market prediction based on sentiment analysis
const sentimentData = $input.all()[0].json;

function predictMarketDirection(sentimentScore, bullishCount, bearishCount, totalSources) {
  let prediction = 'hold';
  let confidence = 0;
  let reasoning = [];

  // Strong bullish sentiment
  if (sentimentScore > 0.5 && bullishCount > bearishCount * 2) {
    prediction = 'strong_buy';
    confidence = Math.min(90, 60 + (sentimentScore * 30));
    reasoning.push('Strong bullish sentiment detected across multiple sources');
  }
  // Moderate bullish sentiment
  else if (sentimentScore > 0.2) {
    prediction = 'buy';
    confidence = Math.min(75, 50 + (sentimentScore * 25));
    reasoning.push('Moderate positive sentiment in the market');
  }
  // Strong bearish sentiment
  else if (sentimentScore < -0.5 && bearishCount > bullishCount * 2) {
    prediction = 'strong_sell';
    confidence = Math.min(90, 60 + (Math.abs(sentimentScore) * 30));
    reasoning.push('Strong bearish sentiment detected - consider exiting positions');
  }
  // Moderate bearish sentiment
  else if (sentimentScore < -0.2) {
    prediction = 'sell';
    confidence = Math.min(75, 50 + (Math.abs(sentimentScore) * 25));
    reasoning.push('Negative sentiment trend - caution advised');
  }
  // Neutral/mixed signals
  else {
    prediction = 'hold';
    confidence = 40;
    reasoning.push('Mixed signals in market sentiment - maintain current positions');
  }

  // Adjust confidence based on data volume
  if (totalSources < 10) {
    confidence *= 0.8;
    reasoning.push('Limited data sources - lower confidence');
  }

  return {
    prediction,
    confidence: Math.round(confidence),
    reasoning
  };
}

const marketPrediction = predictMarketDirection(
  sentimentData.sentiment_score,
  sentimentData.bullish_count,
  sentimentData.bearish_count,
  sentimentData.total_sources
);

// Generate investment recommendations
let recommendations = [];
switch(marketPrediction.prediction) {
  case 'strong_buy':
    recommendations = [
      'Consider increasing real estate investment allocation',
      'Look for value opportunities in growth markets',
      'Accelerate planned property acquisitions'
    ];
    break;
  case 'buy':
    recommendations = [
      'Moderate increase in real estate exposure',
      'Focus on cash-flowing properties',
      'Monitor market closely for entry points'
    ];
    break;
  case 'strong_sell':
    recommendations = [
      'Consider reducing real estate exposure',
      'Postpone new acquisitions',
      'Review portfolio for underperforming assets'
    ];
    break;
  case 'sell':
    recommendations = [
      'Exercise caution with new investments',
      'Consider taking profits on recent gains',
      'Increase cash reserves'
    ];
    break;
  default:
    recommendations = [
      'Maintain current investment strategy',
      'Continue regular market monitoring',
      'Stay prepared for market shifts'
    ];
}

return [{
  json: {
    ...sentimentData,
    market_prediction: marketPrediction.prediction,
    confidence_level: marketPrediction.confidence,
    reasoning: marketPrediction.reasoning,
    investment_recommendations: recommendations,
    risk_level: marketPrediction.confidence > 70 ? 'low' : marketPrediction.confidence > 50 ? 'medium' : 'high',
    prediction_timestamp: new Date().toISOString()
  }
}];

// Timing optimization based on market prediction and historical patterns
const predictionData = $input.all()[0].json;

function optimizeTiming(prediction, confidence, sentimentScore) {
  let timing_recommendation = 'monitor';
  let urgency = 'low';
  let optimal_timeframe = '1-3 months';
  let timing_factors = [];

  // Determine optimal timing based on prediction strength
  if (prediction === 'strong_buy' && confidence > 75) {
    timing_recommendation = 'act_immediately';
    urgency = 'high';
    optimal_timeframe = '1-2 weeks';
    timing_factors.push('Strong market signals indicate immediate action needed');
  }
  else if (prediction === 'buy' && confidence > 60) {
    timing_recommendation = 'act_soon';
    urgency = 'medium';
    optimal_timeframe = '2-4 weeks';
    timing_factors.push('Positive sentiment building - act within optimal window');
  }
  else if (prediction === 'strong_sell') {
    timing_recommendation = 'exit_positions';
    urgency = 'high';
    optimal_timeframe = '1-2 weeks';
    timing_factors.push('Strong negative signals - consider immediate exit strategy');
  }
  else if (prediction === 'sell') {
    timing_recommendation = 'reduce_exposure';
    urgency = 'medium';
    optimal_timeframe = '2-6 weeks';
    timing_factors.push('Negative trend developing - gradual position reduction advised');
  }

  // Add seasonal considerations (simplified)
  const currentMonth = new Date().getMonth();
  const seasonalFactors = {
    spring: [2, 3, 4], // March, April, May
    summer: [5, 6, 7], // June, July, August
    fall: [8, 9, 10],  // September, October, November
    winter: [11, 0, 1] // December, January, February
  };

  let currentSeason = 'winter';
  for (const [season, months] of Object.entries(seasonalFactors)) {
    if (months.includes(currentMonth)) {
      currentSeason = season;
      break;
    }
  }

  if (currentSeason === 'spring' || currentSeason === 'summer') {
    timing_factors.push('Peak real estate season - higher activity expected');
  } else {
    timing_factors.push('Off-season period - potentially better deals available');
  }

  return {
    timing_recommendation,
    urgency,
    optimal_timeframe,
    timing_factors,
    current_season: currentSeason
  };
}

const timingAnalysis = optimizeTiming(
  predictionData.market_prediction,
  predictionData.confidence_level,
  predictionData.sentiment_score
);

return [{
  json: {
    ...predictionData,
    timing_recommendation: timingAnalysis.timing_recommendation,
    urgency_level: timingAnalysis.urgency,
    optimal_timeframe: timingAnalysis.optimal_timeframe,
    timing_factors: timingAnalysis.timing_factors,
    current_season: timingAnalysis.current_season,
    timing_analysis_timestamp: new Date().toISOString()
  }
}];

