const inputData = $input.first().json;

// WhatsApp Message Processing - Based on your actual data structure
if (inputData.messaging_product === 'whatsapp' && inputData.messages && inputData.messages[0]) {
  return {
    platform: 'whatsapp',
    senderId: inputData.contacts[0].wa_id,
    senderName: inputData.contacts[0].profile.name || 'User',
    message: inputData.messages[0].text.body,
    messageId: inputData.messages[0].id,
    timestamp: inputData.messages[0].timestamp
  };
}

// Instagram DM Processing
if (inputData.object === 'instagram' && inputData.entry && inputData.entry[0]) {
  const entry = inputData.entry[0];
  const messaging = entry.messaging ? entry.messaging[0] : null;

  if (messaging) {
    return {
      platform: 'instagram',
      senderId: messaging.sender.id,
      senderName: 'Instagram User',
      message: messaging.message.text,
      messageId: messaging.message.mid,
      timestamp: messaging.timestamp || Date.now()
    };
  }
}

// Facebook Messenger Processing
if (inputData.object === 'page' && inputData.entry && inputData.entry[0]) {
  const entry = inputData.entry[0];
  const messaging = entry.messaging ? entry.messaging[0] : null;

  if (messaging) {
    return {
      platform: 'facebook',
      senderId: messaging.sender.id,
      senderName: 'Facebook User',
      message: messaging.message.text,
      messageId: messaging.message.mid,
      timestamp: messaging.timestamp
    };
  }
}

// Default WhatsApp fallback based on your current data
return {
  platform: 'whatsapp',
  senderId: inputData.contacts ? inputData.contacts[0].wa_id : 'unknown',
  senderName: inputData.contacts ? (inputData.contacts[0].profile.name || 'User') : 'Unknown User',
  message: inputData.messages ? inputData.messages[0].text.body : 'Hello',
  messageId: inputData.messages ? inputData.messages[0].id : 'unknown',
  timestamp: inputData.messages ? inputData.messages[0].timestamp : Date.now()
};
