function kbToMb(kb) {
    return (kb / 1024).toFixed(2); // Convert KB to MB
}

function bytesToKb(bytes) {
    return bytes / 1024;  // Convert Bytes to KB
}

// Get the file size in bytes from the input (from the Google Drive node)
let fileSizeInBytes = $('Google Drive').first().json.size;

// Convert bytes to KB first
let fileSizeInKb = bytesToKb(fileSizeInBytes);

// Then convert KB to MB
let fileSizeInMb = kbToMb(fileSizeInKb);

let outputData = {
    fileName: $input.first().json.name,
    fileSizeInMb: fileSizeInMb
};

// Return an array containing the output data
return [{ json: outputData }];
