// Filter active clients ready for invoicing
const allClients = $input.all();
const today = new Date();
today.setHours(0, 0, 0, 0);

const clientsToInvoice = allClients.filter(item => {
  const client = item.json;
  // Skip header row
  if (client.A === 'client_id' || !client.A) return false;

  const status = client.F; // Column F = status
  const billingDate = new Date(client.E); // Column E = billing_date
  billingDate.setHours(0, 0, 0, 0);

  return status === 'active' && billingDate <= today;
});

// Transform to readable format
const transformedClients = clientsToInvoice.map(item => {
  const client = item.json;
  return {
    json: {
      client_id: client.A,
      client_name: client.B,
      email: client.C,
      service_description: client.D,
      billing_date: client.E,
      status: client.F,
      amount: parseFloat(client.G) || 0
    }
  };
});

return transformedClients;

// Filter overdue invoices
const allInvoices = $input.all();
const today = new Date();
today.setHours(0, 0, 0, 0);

const overdueInvoices = allInvoices.filter(item => {
  const invoice = item.json;
  // Skip header row
  if (invoice.A === 'invoice_id' || !invoice.A) return false;

  const status = invoice.G; // Column G = status
  const dueDate = new Date(invoice.F); // Column F = due_date
  dueDate.setHours(0, 0, 0, 0);

  // Get yesterday's date
  const yesterday = new Date(today);
  yesterday.setDate(yesterday.getDate() - 1);

  return status === 'pending' && dueDate <= yesterday;
});

// Transform to readable format
const transformedInvoices = overdueInvoices.map(item => {
  const invoice = item.json;
  return {
    json: {
      invoice_id: invoice.A,
      invoice_number: invoice.B,
      client_name: invoice.C,
      email: invoice.D,
      amount: parseFloat(invoice.E) || 0,
      due_date: invoice.F,
      status: invoice.G,
      last_reminder_sent: invoice.H,
      reminder_count: parseInt(invoice.I) || 0
    }
  };
});

return transformedInvoices;

// Generate invoice number
const now = new Date();
const invoiceNumber = `INV-${now.getFullYear()}-${String(now.getMonth() + 1).padStart(2, '0')}-${String(now.getDate()).padStart(2, '0')}-${String(Math.floor(Math.random() * 1000)).padStart(3, '0')}`;

// Calculate due date (30 days from now)
const dueDate = new Date();
dueDate.setDate(dueDate.getDate() + 30);

// Get client data
const clientData = $input.first().json;

// Create invoice data
const invoiceData = {
  invoice_number: invoiceNumber,
  client_id: clientData.client_id,
  client_name: clientData.client_name,
  email: clientData.email,
  service_description: clientData.service_description,
  amount: clientData.amount,
  issue_date: now.toISOString().split('T')[0],
  due_date: dueDate.toISOString().split('T')[0],
  status: 'pending'
};

return { json: invoiceData };

// Calculate days overdue
const dueDate = new Date($json.due_date);
const today = new Date();
const daysOverdue = Math.floor((today - dueDate) / (1000 * 60 * 60 * 24));

// Determine reminder type based on days overdue
let reminderType = 'gentle';-
let subject = 'Payment Reminder';

if (daysOverdue >= 30) {
  reminderType = 'final';
  subject = 'FINAL NOTICE - Payment Required';
} else if (daysOverdue >= 14) {
  reminderType = 'urgent';
  subject = 'URGENT - Payment Overdue';
} else if (daysOverdue >= 7) {
  reminderType = 'follow-up';
  subject = 'Payment Follow-up Required';
}

return {
  json: {
    ...($json),
    days_overdue: daysOverdue,
    reminder_type: reminderType,
    email_subject: subject
  }
};
